import React from 'react';
import './App.css';

import Fingerprint2 from "SimpleChatbot-1.3.2/chatbot/fp2"
import SimpleChatbot from "SimpleChatbot-1.3.2/chatbot/chatbot"

const configChatbot = {};
configChatbot.btn = '.chatbot__btn';
configChatbot.key = 'fingerprint';
configChatbot.replicas = '/data/data-1.json';
configChatbot.root = SimpleChatbot.createTemplate();
configChatbot.url = '/chatbot/chatbot.php';
let chatbot = null;
let fingerprint = localStorage.getItem(configChatbot.key);
if (!fingerprint) {
Fingerprint2.get(function (components) {
    fingerprint = Fingerprint2.x64hash128(components.map(function (pair) {
    return pair.value
    }).join(), 31)
    localStorage.setItem(configChatbot.key, fingerprint)
});
}
document.querySelector(configChatbot.btn).onclick = function (e) {
this.classList.add('d-none');
const $tooltip = this.querySelector('.chatbot__tooltip');
if ($tooltip) {
    $tooltip.classList.add('d-none');
}
configChatbot.root.classList.toggle('chatbot_hidden');
if (chatbot) {
    return;
}
const request = new XMLHttpRequest();
request.open('GET', configChatbot.replicas, true);
request.responseType = 'json';
request.onload = function () {
    const status = request.status;
    if (status === 200) {
    const data = request.response;
    if (typeof data === 'string') {
        configChatbot.replicas = JSON.parse(data);
    } else {
        configChatbot.replicas = data;
    }
    chatbot = new SimpleChatbot(configChatbot);
    chatbot.init();
    } else {
    console.log(status, request.response);
    }
};
request.send();
};

function App (){
    return (
        <div className="App">
            <div class="chatbot__btn">
                <div class="chatbot__tooltip d-none">Есть вопрос?</div>
            </div>
        </div>
    );
};

export default App;
