import React, { useState } from 'react';
import Creatable from "react-select";
import { components } from "react-select";

import './App.css';


const en_type_choice_data = {
  "technician" : [
    {
      label: "Робототехника",
      value: "Робототехника"
    },
    {
      label: "Программирование",
      value: "Программирование"
    },
    {
      label: "Аддитивные технологии",
      value: "Аддитивные технологии"
    },
    {
      label: "VR/AR",
      value: "VR/AR"
    },
    {
      label: "Аэро",
      value: "Аэро"
    },
    {
      label: "Дизайн",
      value: "Дизайн"
    }
  ],
  "scientist" : [
    {
      label: "Экология",
      value: "Экология"
    },
    {
      label: "Физиология",
      value: "Физиология"
    },
    {
      label: "Биохимия",
      value: "Биохимия"
    },
    {
      label: "Биотехнолгии ",
      value: "Биотехнолгии "
    }
  ],
  "artist" : [
    {
      label: "Кукольный театр",
      value: "Кукольный театр"
    },
    {
      label: "Литературный театр",
      value: "Литературный театр"
    },
    {
      label: "Драматический театр",
      value: "Драматический театр"
    },
    {
      label: "Художественное слово/ ораторское мастерство ",
      value: "Художественное слово/ ораторское мастерство "
    }
  ],
  "tourist" : [
    {
      label: "Основы туризма",
      value: "Основы туризма"
    },
    {
      label: "Краеведение",
      value: "Краеведение"
    },
    {
      label: "школа безопасности",
      value: "школа безопасности"
    }
  ]
}

const Menu = (props) => {
  const optionSelectedLength = props.getValue().length || 0;
  return (
    <components.Menu {...props}>
      {optionSelectedLength < 2 ? (
        props.children
      ) : (
        <div style={{ margin: 15 }}>Max limit achieved</div>
      )}
    </components.Menu>
  );
};

function App (){
  const [value, setValue] = useState('');
  return (
    <div className="App">
      <form action="http://localhost:5000/test" method = "POST">
        <div className="forms">
          <h1>Пояснительная записка</h1>
          <label>Программа имеет направленность </label>
          <select type="text" name="en_orientation" value={value} onChange={(event) => setValue(event.target.value)}>
            <option value="technician">Техническая</option>
            <option value="scientist">Естественнонаучная</option>
            <option value="artist">Художественная</option>
            <option value="tourist">Туристско-краеведческая</option>
          </select><br/>
          <label>Вид деятельности </label>
          <select type="text" name="en_type_activity">
            {en_type_choice_data[value].map(
              ({ value, label }) => <option key={value} value={value}>{label}</option>
            )}
          </select><br/>
          <label>
            Актуальность  — это ответ на вопрос, зачем современным детям в современных условиях нужна конкретная программа (своевременность, современность). Актуальность отражает:
             актуальность для общества (как ребёнок с приобретёнными компетенциями будет востребован в современном обществе),
             актуальность для ребёнка (что ребёнок приобретет и как изменится).
            (не более 1000 знаков)
          </label><br/>
          <textarea />
          {/* <Creatable
            name="test"
            components={{ Menu }}
            isMulti
            options={options}
          /> */}
        </div>
        <div className = "button">
          <input type="submit" method="POST"/>
        </div>
      </form>
    </div>
  );
}

export default App;
