import React, { Component, useState } from 'react';
import './App.css';

const App = () => {
  // const [value, setValue] = useState("some text");
  // const [checked, setChecked] = useState(false);
  const [value, setValue] = useState("НМС");

	const handlerChange = (e) => {
		setValue(e.target.value);

		console.log(e.target.value);
	};
  return (
    <div className="App">
      <div className='Title List'>
        <label>Учредитель образовательной организации</label><br/>
        <input value="И.О.Фамилия"/><br/>
        <label>Полное название образовательной организации (по уставу)</label><br/>
        <input value=""/><br/>
        <label>Сокращенное название образовательной организации (по уставу)</label><br/>
        <input value=""/><br/>
        <label>Рассмотрено на </label>
        <select value={value} onChange={handlerChange}>
          <option value="НМС">НМС</option>
          <option value="ПС">ПС</option>
        </select>
      </div>
      {/* {value}<br/>
      <input value={value} onChange={(event) => {setValue(event.target.value)}}/><br/>
      <label>some text</label>
      <input type="checkbox" value={checked} onChange={() => setChecked(!checked)}/><br/> */}
    </div>
  );
}

export default App;
