import React from 'react';
import Creatable from "react-select";
import { components } from "react-select";

import './App.css';

const options = [
  {
    label: "option 1",
    value: 1
  },
  {
    label: "option 2",
    value: 2
  },
  {
    label: "option 3",
    value: 3
  },
  {
    label: "option 4",
    value: 4
  }
];

const Menu = props => {
  const optionSelectedLength = props.getValue().length || 0;
  return (
    <components.Menu {...props}>
      {optionSelectedLength < 2 ? (
        props.children
      ) : (
        <div style={{ margin: 15 }}>Max limit achieved</div>
      )}
    </components.Menu>
  );
};

function App (){
  return (
    <div className="App">
      <form action="http://localhost:5000/test" method = "POST">
        <div className="forms">
          <Creatable
            name="test"
            components={{ Menu }}
            isMulti
            options={options}
          />
        </div>
        <div className = "button">
          <input type="submit" method="POST"/>
        </div>
      </form>
    </div>
  );
}

export default App;
